CREATE TABLE IF NOT EXISTS `energia` (
  `id` int(11) NOT NULL,
  `nombre` varchar(25) NOT NULL,
  `significado` tinytext NOT NULL,
  `htmlCodigo` mediumtext NOT NULL,
