INSERT INTO `kins` (`id`, `iddesk`, `nombre`, `significado`, `htmlCodigo`, `categoria`, `nombreYucateco`, `ruta`) VALUES
(0, 1, 'Imix\'', 'Awaj darb’', 'Are ri uchijij ri k’aslemal rech noj chomanik’ are k’ik’i¿otik’ rech uj k’eb’ sib’alaj noj wuj xuquje re k’ib’anak’ ri  nim nim ajtijab’ ajq¿ij k’e b’ixonik pa le uwach ulew k’isk’i¿otik¿ re le nim ja’ sib’alaj k’ub’anak’ rech le nim wachch re chak’.', 'Kin_0', 'Imix', 'Calendario_Haab\\Kines\\Imix.png'),
(1, 2, 'Ik\'', 'Tik’ik’', 'Are ri tinamit’ rech le k’eb’ rech uwacch noj rech uchomanik’ k’echawik’ le winaq’ jupuq’ xuquje re le nim ja’ are k’ek’ik’olok’ pa le job’al t’ij rech nim wachch k’olik.', 'Kin_1', 'Ik', 'Calendario_Haab\\Kines\\Ik.jpg'),
(2, 3, 'Ak\'bal\'', 'Chaq\'ab\'', 'Le q’ij le sutz’ q’eq’ ri k’aslemal joropa le ri k’ewarik’ k’ix chob’anik’ le tz’ij xuquje le nim paja’ are ri job’al chach sib’alaj nim taq’ maj rech paq’ nim ajaw re le nim paq’ik’ are k’e paxanik’.', 'Kin_2', 'Akbal', 'Calendario_Haab\\Kines\\Akbal.jpg'),
(3, 4, 'K\'an', 'Le ija’ re ixim ', 'Are uwach le  ub’aq’il re k’aslemal are k’uya uwach le ija’ re ixim sib’alaj xuquje ri chob¿’aj nim uwach re le uwach re le ub’aq’l ri ajxotenik’ re le nim pa ja’ñ', 'Kin_3', 'Kan', 'Calendario_Haab\\Kines\\Kan.png'),
(4, 5, 'Chikchan', 'K’umatz’', 'Are ri paj re le ri k’aslemal uwach ri b’aj re le paj k’ya le nim k’aslemal are k’uya rech le nim paq’ tre le pajib’al k’uya ri  chuchuj re nim paja re nim wachch are k’uya le xuquje le sib’alaj.', 'Kin_4', 'Chicchan', 'Calendario_Haab\\Kines\\Chikchan.png'),
(5, 6, 'Kimi', 'Ri uwach rech siwan tinamit', 'Are ri q’amab’ re le nim ik’ ri aj mayib’ rtech palaj sib’alaj potz’ rech paloj kex k’amik’ npa le noj chomanik’ik re le k’aslemal uwach re le nim wacholil le noj tz’b’.', 'Kin_5', 'Cimi', 'Calendario_Haab\\Kines\\Kimi.png'),
(6, 7, 'Manik\'', 'Le uq’ab’', 'Ri aq’ab’aj re le uwach re le k’unanik’ k’ek’omik’ keb’ rech le uq’ab’aj re uwarik’ uk’asiq’ik’ are k’unenik’ urox kuya maj re lke paq’ re nim wachch masis.', 'Kin_6', 'Manik', 'Calendario_Haab\\Kines\\Manik.png'),
(7, 8, 'Lamat', 'Ri chumil ', 'Ri chomil ruya wach le je’lik k’ik’otik’ k’uya uchomanik’ ri win aq’ kwilo le chumil xuquje ri are k’uya ri maj k’echaq’k’ le nim paja’ k’uya le uwach le k’ib’j le le uwach sib’alaj utz utz.', 'Kin_7', 'Lamat', 'Calendario_Haab\\Kines\\Lamat.png'),
(8, 9, 'Muluk', 'Le ik’', 'Ri watit’ ik’ k’una wonojel ri wanq’q’ k’eb’anoq kuya ri chonamik’ rech pachinik, are ri chojonik’ k’ik’otik’ rech maj utz utz k’uya le chcuchuq’ ri k’uya le tz’ij rech masis q’aqink’uxatik’ rech nim paq’.', 'Kin_8', 'Muluc', 'Calendario_Haab\\Kines\\Muluk.png'),
(9, 10, 'Ok', 'le tz’i', 'Are uchuq’ab’ rech wanima . le tz’I k’uya le sib’laj utz le uq’ chijij junre ajaw are ri k’uya le uwach re le uchajinik’ are k’uchaq’iq’ le nim rech wach sib’alaj chijinik’ pa le paq’ k’e tionik’.', 'Kin_9', 'Oc', 'Calendario_Haab\\Kines\\Ok.png'),
(10, 11, 'Chuwen', 'Le k’oy', 'Are ria k¿’al uchomanik’ k’uya le k’aslemal rech q’aq’ k’uya le uwach are chomanik’ le wach xuquje le palj ri nim k’uj b’antajiq’ ri chomanik’ re le watit’ k’aman’ tz’e ri tolpinik’  le iz’el.', 'Kin_10', 'Chuen', 'Calendario_Haab\\Kines\\Chuwen.png'),
(11, 12, 'Eb\'', 'Ri winaq', 'Are ri nim k’i¿uya ub’xik’ ri le uwach le are maj k’ub’anaq’ re le nim paq’ rech le k’ub’antajiq’ re le njim  pachcihc le nim pachoq’ maj ix rech b’ech.', 'Kin_11', 'Eb', 'Calendario_Haab\\Kines\\Eb.png'),
(12, 13, 'B\'en ', 'Ri b’e rech uk’ux re uk’aj ', 'Are ri b’aj re le nim paq’ uwaxch le nim paja re ulew rech ulew ri b’e ri le nim u tzij k’uya le nim paq’ re maj paq’ chioch le ub’e rech ajmayib’.', 'Kin_12', 'Ben', 'Calendario_Haab\\Kines\\Ben.png'),
(13, 14, 'Ix', 'Le sub’laj tz’ij ', 'Are k’uya uwach le itz’el taq’ k’ub’anaq re le k’unenik’ are k’uya le mix are k’uya le nim  aq’ rech le uwachij re le nim upolaxik’ ri xuquje le reche k’e plaj.', 'Kin_13', 'Ix', 'Calendario_Haab\\Kines\\Ix.png'),
(14, 15, 'Men', 'Le nim tz’ik’in', 'Are k’uya le kwile jela are ri rech unoj k’uya le nim k’ik’olik’ ri ub’uxik’ le paq’ rech palaq’ ruk ‘le taq’ pachib’.', 'Kin_14', 'Men', 'Calendario_Haab\\Kines\\Men.png'),
(15, 16, 'Kib\'', 'Le chak’unik’', 'Ri uwach are k’enemenik’ are pa le nim ruk’ le rech k’uya ri uwach le ub’ixik’ le nim ajmayib’ rech le  im paq’’ rech q’aq’ rech le nim paq’ ruk’ le k’ub’onoq’ le nim wachch.', 'Kin_15', 'Cib', 'Calendario_Haab\\Kines\\Kib.png'),
(16, 17, 'Kab\'an', 'Le ulew', 'Are k’uya le uwach ulew k’uya le are ri k’ub’j le nimtzij rech le nim wachcihch. Re le maj q’ej k’uya le uchoq’ab’ trech le nim.', 'Kin_16', 'Caban', 'Calendario_Haab\\Kines\\Kaban.png'),
(17, 18, 'Etz\'anab\'', 'Ri uwach k’ek’ayik’ ri ab’aj rech palaj rech ri nim paq’ rech le nim wachch ', 'Ri q’q’ rech le nim k’uya le k’aslemal rech nim paq’ k’uya ri uchuq’ab’ rech le nim awaj rech le maj tzij rech palaj rech le nim ab’aj k’uya ri uchuq’ab’ rech maj rech le nim ja’ rech le waj rech palaj reche de le nim awaj jolib’al rech le nim  ja’kuya uchuq’ab’.', 'Kin_17', 'Etznab', 'Calendario_Haab\\Kines\\Etznab.png'),
(18, 19, 'Kawak', 'Le ri job’', 'Ri wach le job’ ri chob’anik’ k’uj b’inik’ wach ulew k’epetik’ le jo’ rech ,maj k’e banoq’ le maj k’i¿olik’ ri uwach ri ub’ej che le mj sib’laj moch utz le uwach rech sib’alaj utrz le q’aq’ rech ri k’aslemal rech uwach ulew maj xuquje re ulew,', 'Kin_18', 'Cauac', 'Calendario_Haab\\Kines\\Kawak.png'),
(19, 20, 'Ajaw', 'Le q’ij ri q’ij ', 'are uwach le q’aq’ re le k’aslemal rech ri chomil k’e b’xonik’ ri k’amalb’ are ri k’echiplak’ib’ are o usuk’ k’achak’unik’ ri are ri awaj rech le nim q’ij rech k’uk’ le nim wach xib’inik’,', 'Kin_19', 'Ahau', 'Calendario_Haab\\Kines\\Ajaw.png');


