INSERT INTO `nawal` (`idweb`, `iddesk`, `nombre`, `nombreYucateco`, `significado`, `htmlCodigo`, `categoria`, `descripcion`, `ruta`) VALUES
(0, 15, 'Tzikin', NULL, 'Tzi', '<strong>Tzij wuj rech </strong>\n<p>\n  \n Ri k’ub’ij ri tz’k’in are k’uya le uwach le ri k’asleml wa’ xuquje ri\n  nawal rech ri k’echopanik’ rech chomanik’ k’uk’umatz’ rech paq rajil rech ajaw\n  re le nim q’ij tz’k’in ri waq rech maj wanima rech ruk’ le waq’b’ chab’al rech\n  maj tzij rech maj rech le min q’ij. Xuquje ri q’ij rech jun awaj ri plaj rech\n  ri rajil rech xuquje re le nim chuch aq’q’ rik’.r\n\n</p>\n\n\n\n<p>\n <strong>Función Espiritual:</strong>\n</p>\n\n\n<ul>\n  \n\n  <li>sib’alaj maltyoxiq’ rech taq’ ri chab’anik’ rech rumal rajil rech</li>\n  \n\n  <li>q\'ij xuquje ri ri chak’uk\'nik’ rech le siwan tinamit’</li>\n  \n\n  <li>q’ij rech le nim q’ij re ija’ re rech le awaj.</li>\n  \n\n</ul>\n\n\n<p><strong>Su origen:</strong> Kej <strong>wanima:</strong> Aq\'ab\'al</p>\n\n\n<p>\n <strong>Fortalezas:</strong>\n</p>\n\n\n<ul>\n  \n\n  <li>ech ri k’ub’anik’ le chak’</li>\n  \n\n  <li>rech jun jujun nim aq’b’</li>\n  \n\n  <li>chomanik ri warib’a.</li>\n  \n\n  <li>utz ri warib’al</li>\n  \n\n  <li>rech xuquje ri ajq’ij</li>\n  \n\n</ul>\n\n\n<p>\n <strong>Debilidades:</strong>\n</p>\n\n\n<ul>\n  \n\n  <li>iz’el</li>\n  \n\n  <li>maj infat’</li>\n  \n\n  <li>k’elek\'anik’</li>\n  \n\n  <li>rich tijowik’</li>\n  \n\n</ul>\n\n', 'Nahual_0', '', 'Calendario_Cholquij\\Nahuales\\cholquij-tzikin.png'),
(1, 16, 'Ajmaq', NULL, 'k’oo', '<strong>Rech </strong>\n<p>\n  \r\n Bubu tuk’ur iz’el \r\n Ri tzuij ri re q’ij rech k’uya k’uj pech rech\n  k’umanik’ rech uwach ulew ri ajaw ri maj ri ojer taq winaq’ rech wuq’ub’ rech\n  le winaq’. Re nim q’ij re uwach ulew rech plaj rech plaj k’ur jun mul\n  chak’unik’ pa le winaq’ re sib’ak¿laj noj wuj rech k’extiq’ riwal le\n  k’amanaq’ij ajmaq’ ri maj utz’al ri awaj are k’uya ri chpmanik’ ri paq’ rech\n  iz’el taq’ k’ichomaj rech k’ar taq’.r\n\n</p>\n\r\n\n\n\r\n\n<p>\r\n <strong>Fortalezas:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>are k’uya ri ub’ixik’ cghe le k’ab’naq’ib’ rech winaq’</li>\n  \r\n\n  <li>re arech k’uya rech chomanik’</li>\n  \r\n\n  <li>urok’ chojinik’</li>\n  \r\n\n  <li>are k’uchapiq’ ru uchi</li>\n  \r\n\n  <li>maj utz le siwan wunaq’</li>\n  \r\n\n  <li>k’ub’ij taq’ prej</li>\n  \r\n\n  <li>chak’unik’</li>\n  \r\n\n  <li>chwik’</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Debilidades:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>iz\'el winaq’</li>\n  \r\n\n  <li>Rech wanima</li>\n  \r\n\n  <li>rech maj rech le waniq’</li>\n  \r\n\n  <li>maj k’k’ubij taq’</li>\n  \r\n\n  <li>manoq’</li>\n  \r\n\n  <li>ri floj wach rech maj re tzij</li>\n  \r\n\n  <li>are k’amalb’e rech tinamit’.</li>\n  \r\n\n</ul>\n', 'Nahual_1', '', 'Calendario_Cholquij\\Nahuales\\cholquij-ajmaq.png'),
(2, 17, 'No\'j', NULL, 'Chomanik', '<strong>Rech</strong>\n<p>\n  \r\nAre chomanik’ rech chomanik’ re le watit’ kaman rech ri ajaw rech xuquje\n  re le nim ajaw chomanik’ re le chomanik’ ri k’awal uwach chuch le noj wuj rech\n  le kuj b’ixonik’ rech plaj re nim q’ij ri q’aq’ rech ajmayib’ rech le nim q’ij\n  ri awaj xuk’ulem re nim ajmayib’ rech maj tzij ajq’ij rech le nim taq’ ajq’ij\n  re le nim winaq’ rech le nim awaj rech plaj re le nim wuj chomanik’ no’j ri\n  q’ij ri xuquje re le nim q¿ij are k’uya ri chob’ab’alaj ri utz rech le q’ij\n  k’awik’chomanik’k’ rech le nim q’ij..r\n\n</p>\n\r\n\n\n<p>\r\n <strong>Función Espiritual:</strong>\r\n Origen toj rech kan</p>\n\r\n\n<ul>\n  \r\n\n  <li>ri ajaw rech le nim chomanik’ rech le nimq’ij</li>\n  \r\n\n  <li>are k’uya ri uwach ri uwach</li>\n  \r\n\n  <li>q’ij rech maj re wunaq’</li>\n  \r\n\n  <li>are k’uya ri sib’alaj utz ri waniq’</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Fortalezas:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>are kuj chomanik re nim q’ij chomanik’</li>\n  \r\n\n  <li>are ri chomaj ri ri k’amalb’e rech</li>\n  \r\n\n  <li>k’uya ri iuwach</li>\n  \r\n\n  <li>are k’ya ri waniq’</li>\n  \r\n\n  <li>are tijoxel</li>\n  \r\n\n  <li>k’amalb’e rech</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Debilidades:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>Are k’uj xoboixik’ ri wanik’</li>\n  \r\n\n  <li>are k’echomanik’</li>\n  \r\n\n  <li>ri chomanik’</li>\n  \r\n\n  <li>rech irj iz’el winaq’</li>\n  \r\n\n</ul>\n', 'Nahual_2', '', 'Calendario_Cholquij\\Nahuales\\cholquij-noj.png'),
(3, 18, 'Tijax', NULL, 'ch’aak', '<strong>Rech</strong>\n<p>\n  \r\nTzij ri ab’aj re uchaq’ab’ rech le nim ab’ ri k’awaj k’awaw’ ri tek’olja\n  rech maj are k’uy jun nim kunanik’ rech ajq’ij, rech q’ab’aj re le nim ajaw ri\n  b’ak’il rech le nim q’ij ri jujun re le nim q’ij rech ajq’ijq rech ri\n  chomanik’ rech le q’ij.r\n\n</p>\n\r\n\n\n<p>\r\n <strong>Fortalezas:</strong>\r\n Origen Tijax rech keme</p>\n\r\n\n<ul>\n  \r\n\n  <li>\n    Ri q’ij re le nim k’uya ri ub’ixik’ che le nim awaj ri wach re le nim wach\n    re min q’ij rech paq’\n  </li>\n  \r\n\n  <li>Ruk’ ri uchuq’ab’ tz’I</li>\n  \r\n\n  <li>are k’uya ri uchab’exik’ ri tz’aj</li>\n  \r\n\n  <li>uwach uk’uch</li>\n  \r\n\n  <li>k’uya ri ub’exik’ ri nim q’ij</li>\n  \r\n\n  <li>ri maj k’echojonik’</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Debilidades:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>Iz’el winaq’</li>\n  \r\n\n  <li>k’uya ri wach</li>\n  \r\n\n  <li>are k’uya le maq’</li>\n  \r\n\n  <li>ri plaj rech k’echojonik’</li>\n  \r\n\n</ul>\n', 'Nahual_3', '', 'Calendario_Cholquij\\Nahuales\\cholquij-tijax.png'),
(4, 19, 'Kawoq', NULL, 'k’a’y', '<strong>Rech</strong>\n<p>\n  \r\nTek’olja’ rech uwach ulew Ri nawal rech imob’ rech imoxib’ ri q’ij rech ri\n  ajamq’ uj k’eb’ q’ij alaxik’ ri jupuq’ re jo’ rech ixim. Ri paq’ rech ri\n  k’unanik’ uchuq’ab’ rech ajq’ij re le nim q’ij nan ulew rech de re k’otz’ij ri\n  wqacho chuchoj xuquje ruk’ ri tinamit’ nrech wanojel.r\n\n</p>\n\r\n\n\n<p>\r\n <strong>Rech ajq’ij.</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>jujun re ri b’aq’il re la axob’ k’exk’olik’</li>\n  \r\n\n  <li>q’ij ri alaxik’ re tinamit’.</li>\n  \r\n\n  <li>q’ij rech ri ak’alab’ k’e maj ri nitz’ ak’al kekolik’</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Fortalezas:</strong>\r\n Ri uwach b’atz’ ri kyej</p>\n\r\n\n<ul>\n  \r\n\n  <li>ri k’ikotik</li>\n  \r\n\n  <li>k’aslemal</li>\n  \r\n\n  <li>ri alaxik’</li>\n  \r\n\n  <li>le ixob’</li>\n  \r\n\n  <li>ri ajb’xoq’</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Debilidades:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>ri caech riwal</li>\n  \r\n\n  <li>ri iz’el taq’ winaq’</li>\n  \r\n\n  <li>maj utz taq’ ri wanima</li>\n  \r\n\n  <li>ri wach tz’ijonik’</li>\n  \r\n\n</ul>\n', 'Nahual_4', '', 'Calendario_Cholquij\\Nahuales\\cholquij-kawoq.png'),
(5, 20, 'Ajpu', NULL, 'Cerbatanero o señor principal', '<strong>Rech</strong>\n<p>\n  \r\nRi awach ulew ri q’q rech watit’ q’ij ria jaw Ri jun ajpun ixb’alanque\n  Ixmuk’ane Ri tik’onik’ k’eb’ achijab’ re upa wa’ rech tinamit’ Ri aq’j\n  uchuq’ab’ rech k’aslemal ri tek’olja ri maj ri chanim rech le tinamit’.r\n\n</p>\n\r\n\n\n<p>\r\n <strong>Rech ajq’ij.</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>q’ijre watit’ kaman</li>\n  \r\n\n  <li>qij rech k’esk’esk’olik’</li>\n  \r\n\n  <li>ri q’ij rech re itz’l</li>\n  \r\n\n  <li>Ruk’ ri q’anil</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Ri wach wuj:</strong>\r\n Ri uwach E\' ri Q\'anil</p>\n\r\n\n<ul>\n  \r\n\n  <li>utz re le nim q’ij</li>\n  \r\n\n  <li>re le nim wach</li>\n  \r\n\n  <li>k’ik’otik’</li>\n  \r\n\n  <li>ri k’exq’ij</li>\n  \r\n\n  <li>ri utz che le ri juyub’</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Ri wach:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>ri caech riwal</li>\n  \r\n\n  <li>ri iz’el taq’ winaq’</li>\n  \r\n\n  <li>maj utz taq’ ri wanima</li>\n  \r\n\n  <li>ri wach tz’ijonik’</li>\n  \r\n\n</ul>\n', 'Nahual_5', '', 'Calendario_Cholquij\\Nahuales\\cholquij-ajpu.png'),
(6, 1, 'Imox', NULL, 'Kar', '<strong>Rech</strong>\n<p>\n  \r\nRi ri uchuq’ab’ re le ulew re le imox re le q’ijn rech tek’takolik’ ri\n  ujolomaj. Ri nawal re ajq’ij k’i¿ux xuquje re winaq re q’ij ri utz le q’ij\n  uwach k’uk’ che le nim Ri waleb’abl ri ja’n ri jolom re chak’anik’ ri ajtij ri\n  q’ij ri le maj ri wach le menta’ k’ex lob’ik’ ri k’aslemal re le ub’ach.r\n\n</p>\n\r\n\n\n<p>\r\n <strong>Rech ajq’ij.</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>ri k’ek’unanik’</li>\n  \r\n\n  <li>q’ij rech invoch</li>\n  \r\n\n  <li>re le nim q’ij chomanik’</li>\n  \r\n\n  <li>ruk’ ri aj toj</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Ri wach wuj:</strong>\r\n Ri uwach Aj ri toj</p>\n\r\n\n<ul>\n  \r\n\n  <li>ruk’ ri wachib’al.</li>\n  \r\n\n  <li>ri chak’unik’</li>\n  \r\n\n  <li>k’alesk’uch</li>\n  \r\n\n  <li>k’uk ri utz taq’</li>\n  \r\n\n</ul>\n\r\n\n<p>\r\n <strong>Ri wach:</strong>\r\n</p>\n\r\n\n<ul>\n  \r\n\n  <li>re le nim ja’</li>\n  \r\n\n  <li>k’uk’ ri chob’el</li>\n  \r\n\n  <li>sib’alaj utz</li>\n  \r\n\n  <li>maj ustq’ ri wach</li>\n  \r\n\n</ul>\n', 'Nahual_6', '', 'Calendario_Cholquij\\Nahuales\\cholquij-imox.png'),
(7, 2, 'Iq\'', '', 'Ik', '<strong>Rech</strong><p> \\r\\nRi tik’ik are ri uk’ux rech le wanima re le re le maj ruk’ le q’ij rech k’aslemal re le winaq’ re uwach le tiq’iq’ rech nawal sib’alaj j’ob’ ri are ulew rech taq’ wonojel ri nojel le maj uztaq’ re le ulew Re aka’l k’il uwach ri tiq’iq’ rech xuquje re klle k’ixpixk’inik’ re nim wach xuquje le majr\\n</p>\\r\\n\n<p>\\r\\n <strong>Rech.</strong>\\r\\n</p>\\r\\n<ul> \\r\\n <li>le q’ij ri utz ri utz che le aq’ij</li> \\r\\n <li>q’ij k’uk’unaq’ ri izel taq’ k’ek’olik’.</li> \\r\\n <li>q’ij rech le ulew k’uya le ija’ ixim</li> \\r\\n</ul>\\r\\n<p>\\r\\n <strong>Ri wach wuj:</strong>\\r\\n Ri uwach I\'x ri T\'zi</p>\\r\\n<ul> \\r\\n\n<li>ruk’ le nim uwach .</li> \\r\\n <li>uchaq’ab’</li> \\r\\n <li>are ri noj chomanik’</li> \\r\\n <li>maj k’uxeb’ tiq’</li> \\r\\n</ul>\\r\\n<p>\\r\\n <strong>Ri wach:</strong>\\r\\n</p>\\r\\n<ul> \\r\\n <li>ri izel winaq</li> \\r\\n <li>maj izet’ utz taq’</li> \\r\\n <li>are k’uya uwach le maj</li> \\r\\n <li>are utz k’uchomaj re le nim maj</li> \\r\\n</ul>\n', 'Nahual_7', '', 'Calendario_Cholquij\\Nahuales\\cholquij-iq.png'),
(8, 3, 'Aq\'ab\'al', '', 'Amanecer, aurora y mano', '<strong>Rech</strong> <p>   \\r\\nRi are utz che le sutz taq’ re le le yujub’ taq’ rech le nim maj   achob’anik’ik’ rech le nim winaq’ Ri q’ij k’amik’ che le nim aq’ab’ ri rech le   paq’ re le taq winaq’ taq’.r\\n </p> \\r\\n  <p>\\r\\n <strong>Rech.</strong>\\r\\n</p> \\r\\n <ul>   \\r\\n   <li>chomaj ri chomanik’</li>   \\r\\n   <li>ruk’ le b’e</li>   \\r\\n   <li>uwach le k’aslemal</li>   \\r\\n   <li>ri q’ij re utz b’e rech nim ojer winaq’</li>   \\r\\n </ul> \\r\\n <p>\\r\\n <strong>utz le winaq’</strong>\\r\\n</p> \\r\\n <ul>   \\r\\n   <li>are k’uya le nim k’exk’olik’</li>   \\r\\n   <li>utz le nim ja’</li>   \\r\\n   <li>moj utz taq’ le mag ulew</li>   \\r\\n   <li>are maj utz taq pa le rajil</li>   \\r\\n   <li>utz taq’ k’uya re le uwach ulew</li>   \\r\\n </ul> \\r\\n', 'Nahual_8', '', 'Calendario_Cholquij\\Nahuales\\cholquij-aqabal.png'),
(9, 4, 'K\'at', '', 'K’at are chupam uwach', '<strong>Rech</strong> <p>   \\r\\Are ri k¿otik’ chipam re le nim utz maj k’uya le nim q’q’ che nim k’aslemal   uwach ri k’at che le k’olik’ ri k’ar taq’ are uchuq’ab’ rech jun tinamit’ Ri   q’at rech le ajq’ij re le nim ajaw re le maj uztq’ re le chob’ik’ le nimajr\\n </p> \\r\\n  <p>\\r\\n <strong>Are k’uya kaslema rech le .</strong>\\r\\n</p> \\r\\n <ul>   \\r\\n   <li>utz uwach</li>   \\r\\n   <li>are kuya le imob’</li>   \\r\\n   <li>q’ij re le kuya le nim b’ixonik’</li>   \\r\\n   <li>utz winaq’</li>   \\r\\n   <li>are kya le maj che uzt/li> \\r\\n</li>    <li>are kuya le nim aj</li>   \\r\\n   <li>are izel wianq’</li>   \\r\\n   <li>are k’ub’ij chel taq’ chij</li>   \\r\\n   <li>are k’ub’ij chel taq’ chij</li>   \\r\\n   <li>are k’uya le chij</li>   \\r\\n   <li>are che le k’utya le nim uztaq’</li>   \\r\\n </ul> \\r\\n ', 'Nahual_9', '', 'Calendario_Cholquij\\Nahuales\\cholquij-kat.png'),
(10, 5, 'Kan', '', 'K’umatz’ che le uplumax', '<strong>Rech</strong> <p>   \\r\\Are kuya uwach che lke q’ij rech le uplumax che le nim awaj re le nim ab’aj   utz che le ik’ taq’ che le nim q’ij k’amik’i Are k’ubij che le mag uztaq’   K’uya le nim mag ustaq’ are che le uchuq’ab’ re le nim ajaw re mag tz’ij re le   ni ajaw iz’et riwal are ajaw rech le utnamit’ are k’uya iz’eltaq wianaq’ñr\\n </p> \\r\\n  <p>\\r\\n <strong>Rech.</strong>\\r\\n</p> \\r\\n <ul>   \\r\\n   <li>are k’uya le nim ez’ab’ech</li>   \\r\\n   <li>q’ij che le nim q’j</li>   \\r\\n   <li>maj utz taq’</li>   \\r\\n   <li>q’ij ri ajq’ij</li>   \\r\\n </ul> \\r\\n <p>\\r\\n <strong>N’oj re le aj</strong>\\r\\n</p> \\r\\n <ul>   \\r\\n   <li>ri che le nim q’ij</li>   \\r\\n   <li>ri uchawaq’</li>   \\r\\n   <li>ri le nim q’j rech</li>   \\r\\n   <li>q’ij</li>   \\r\\n   <li>are le nim</li>   \\r\\n   <li>ri le q’iq’</li>   \\r\\n </ul> \\r\\n ', 'Nahual_10', '', 'Calendario_Cholquij\\Nahuales\\cholquij-kan.png'),
