INSERT INTO `unil` (`idweb`, `nombre`, `significado`, `dias`, `htmlCodigo`, `categoria`, `iddesk`, `ruta`) VALUES
(0, 'Wayeb\'', 'Ri q’ij taq ri abortaq’ k’e chomanik’ maltyox y ri awaj.', 5, '<p>Ri k’oponaq’ ri jo’b’ maj k’o b’iaj ajmayib’ rech k’eb’atinik’ pa ri k’echak’ xuquje re nojel ri jab’al.</p>', 'Uinal_0', 18, 'Calendario_Haab\\Uinal\\haab-wayeb.png'),
(1, 'Pop', 'Mat ri pop ', 20, '<p>are k’o’  k’ojiq’ pa le k’ulnem rech tinamit’ are ri ik’ rech k’ak’ tzij le nim q’ij rech tinamit’ ri k’ekojik’ ri plato’ taq, achiaq’ ri wachcoch ri kab’lajuj q’ij maj k’itik’ taq’ atz’am rech k’ewarik’ are sib’alaj maj uztaq’ ri pare k’eb’ij maj uataq ri pare k’uk’oj chipan re jun tu’y ri q’q rech utz,.</p>', 'Uinal_1', 0, 'Calendario_Haab\\Uinal\\haab-pop.png'),
(2, 'Woo', 'Ixpaq’, q’eq’ ', 20, '<p>Ri chipan le q’ij ri choxol re ajq’ij le ajq’ij k’echomaj ri itzamna rech jan ri ixoq’ ri che re tz’b’ab’al ri k’olb’olb’ bej.</p>', 'Uinal_2', 1, 'Calendario_Haab\\Uinal\\haab-woo.png'),
(3, 'Zip', 'K’ye rech', 20, '<p>Ri choan re q’ij taq’ wonojel ri pari k’emulixik’ik’ chipan ruk le tyox taq’ ri ixchel k’utat’ ria jaw rech ajk’un ri wach le maj k’ebexowaq’ ri chak’u’ki’ noj wuj k’xajowik’ chipam le nim awaj rech le awaj ri chawaq’ rech ri jolomaj re le nim xajowik’ rech le maj ek’ ri waq’ rech le nim awach le q’i8j re le nim ja’ ri ja’ran rech tyaq’ k’ar’ rech ri chopanik’ ri q’ij ri abkanexoi, abpua, xuquje ahcitzamalcun,</p>', 'Uinal_3', 2, 'Calendario_Haab\\Uinal\\haab-zip.png'),
(4, 'Sotz\'', 'rech', 20, '<p>Chipan le q’ij ri k’echopanik’ le nim q’ij rech jujunal re le uwach paj,</p>', 'Uinal_4', 3, 'Calendario_Haab\\Uinal\\haab-sotz.png'),
(5, 'Tzek', 'Ri che taq’j, ri iz’et', 20, '<p>Ri le q’ij are k’echojiq’ le uk’ik’el che le bacabs,  ri ajamtib’ rech hotnil, ajmayib’ rech che taq’ ri sipaq ri uwal che sib’alaj nim.</p>', 'Uinal_5', 4, 'Calendario_Haab\\Uinal\\haab-tzek.png'),
(6, 'Xul', 'Tz’i, b’ixonik’ che le much', 20, '<p>Chipan le q’ij ri kukucan rech ajmayib’ rech masis pop ri sib’alaj winaq’ nacom re ik’ rech tyo ja are ub’I holkanakot, rech tz’I ri ipexixik’ ri tuy taq’ rech ri maj tik’onik’ ri wachch re le nim mayapan rech tz’b’ tz’ij rech le maj oxib’ tyox ja’ uk’ux k’aj uk’ux ulew k’ub’an ri uwach ulew ri rajilk,chik’ab’an,</p>', 'Uinal_6', 5, 'Calendario_Haab\\Uinal\\haab-xul.png'),
(7, 'Yaxk\'in', 'Nab’e q’ij rex upam, rech uwah ija’ jun q’ij k’imik.', 20, '<p>Are che jun ik’ le ri comach re nim ja’n olob zab –kamb’ach ri wonoijel ri ixb’ixonik’ ri tinamit’ ri k’echopanik’ ri uwach q’ij rech le mol.</p>', 'Uinal_7', 6, 'Calendario_Haab\\Uinal\\haab-yaxkin.png'),
(8, 'Mol', 'Ri ik’ rech job’ ri ja’ ri ', 20, '<p>chipan le ik’ ri ri k’etaq’ che le ajaw rech le nim ajaw rech sib’0alaj utz re uwach che re le nim tyox re le nim ajaw rech le b’olaj ri masis le uq’il rech le ixk’in.</p>', 'Uinal_8', 7, 'Calendario_Haab\\Uinal\\haab-mol.png'),
(9, 'Ch\'en', 'Job’ q’eq rech k’a ', 20, '<p>Ri chipam le ik’ ri chipan le nim q’ij k’echomanik’ le tyox taq’ rech masis ri ajmayib’ rech k’onojel ri t’uy taq’ ri ik’ taq’ rib ach re b’j taq’.</p>', 'Uinal_9', 8, 'Calendario_Haab\\Uinal\\haab-chen.png'),
(10, 'Yax', 'Rech, jar’an', 20, '<p>Ri chipan ri ik’ ri tyox ri ajmayib’ rech nitz’ taq’ tuy rech upam le uwach ulew ri wach,</p>', 'Uinal_10', 9, 'Calendario_Haab\\Uinal\\haab-yax.png'),
(11, 'Sak', 'Saq’, job’', 20, '<p>Ri pare taq chuch le k’aslemal rech ajq’ij k’echob’aj ri ajmyib’ ri k’ik’el che le tyox taq’ ri plaj re uq’ik’el rech le nim wanima rech taqw’.</p>', 'Uinal_11', 10, 'Calendario_Haab\\Uinal\\haab-sak.png'),
(12, 'Keh', 'rex mazat\'', 20, '<p>Ri pa q’ij ri nim q’ij sib’alaj utz kle tyx ja’n ri k’uya ri rajil rech. Ayuch rech ab’anik’ k’etob’aq’.</p>', 'Uinal_12', 11, 'Calendario_Haab\\Uinal\\haab-keh.png'),
(13, 'Mak', 'K’at’, rech, pech.', 20, '<p>Ri winaq’ rech ajq’ij re tupp kak k’insaj ri q’aq’ xuquje ri nim q’aq’ ri ja’n ri k’aman tyox ja’ rech ri pare.</p>\n', 'Uinal_13', 12, 'Calendario_Haab\\Uinal\\haab-mak.png'),
(14, 'K\'ank\'in', 'Q’ij q’an', 20, '<p>Ri pa le ik’ ri maj taq’ ri ri ik’ taq ri ajmayib’ che b’inik kur le ik’.a</p>', 'Uinal_14', 13, 'Calendario_Haab\\Uinal\\haab-sak.png'),
(15, 'Muwan', 'Ri chipam le nim q’ij k’eb’an taq’ winaq’ ri q’aq ri tuk’ur’', 20, '<p>Ri chipam ri chopanik’ le nim  tzij rech ri chaxob’ib’ ri maq’ ri ajmayib’ rech le nim rajil ri wa’.</p>', 'Uinal_15', 14, 'Calendario_Haab\\Uinal\\haab-keh.png'),
(16, 'Pax', 'Ri uwach re tik’onik’ ri b’ixonik’ ri ik’ ri le ajq’ij k’e chab’anik che le pacum chach ', 20, '<p>Xuquje rech le nim ulew rech le nim q’ij re le mzat’ re le iz’tzl q’ij rech molul chopmanik’ rech ajq’ij rech masis iz’et¿l taq’ winaq’ reche le nim paja’ ri ajq’ij re k’eb’toxik’ che ri tinamit’.</p>', 'Uinal_16', 15, 'Calendario_Haab\\Uinal\\haab-pax.png'),
(17, 'K\'ayab\'', 'Toq’, nim ajaq’', 20, '<p>Ri maj rech ik taq’ rech tinamit’ rech ri chak’unik’ pa le nim ri rajil rech uayeb’, rech ik’ jpb’ q’ij netsat.Ixim maq’ ri, toja’l wach ja’ ri chaq’ab’ ri ik’ taq’ kayab’ y kumba rech tinamit’ are ri rajil k’echob’axich che le wach re maj rech job’ q’ij tz’ab’.</p>', 'Uinal_17', 16, 'Calendario_Haab\\Uinal\\haab-kayab.png'),
(18, 'K\'umk\'u\'', 'Xa\' jun kukurich, tukul, xikopan.', 20, '<p>Kayab y Kumku ruwi\' taqomel achij tzijonik\' loq\'o\' pa ri tinamit ri ulew, xkib\'aj wi\' ri k\'ayabilthan, xkib\'aj chawe\' ri ruwi\' xuquje winaq\' rech k\'ut le kiwi\', jun ojor xub\'an jun k\'umal, jun tzij no\'jel pa ri q\'ab\'älil kaq kik\'</p>', 'Uinal_18', 17, 'Calendario_Haab\\Uinal\\haab-kumku.png');

