INSERT INTO `pagina` (`orden`, `nombre`, `categoria`, `seccion`, `htmlCodigo`, `idioma`, `seccionOtroIdioma`) VALUES
(0, 'Ajb\'inal', 'Calendario Cholquij', 'Informacion', '<p>Le cholq’ij recch ajmayib’ rech masis \n    <li>chol. Ri ubexik’</li> \n    <li> q’ij ri q’ij</li>\nRi cholq’j ri ajmayib’ rech oxlaj k’alre q’ij rech oxlajuj xuquje re nim cholq’ij maj ri k’eb’lajuj ri k’eb’ oxlajuj ri keb’ ri maj ri jun oxlajuj \nRi cholq’ij ri watit’ kaman ri k’aqtaq ri nim ojer winaq ri chomanik’ rech le nim q’ij rech le pam winaq’ rech paq’ nawal rech ri ajtijab’ ri wajxalajujri cholq’ij ab’ ri maj ri uwach ulew ri q’aq’ ixoq’.\nRi cholpaq’ ri cholq’ij ri maj rech uchoq’ab’ rech ik’ rech ri nojel ri b’aq’il ri ixoq’ ri winaq’ rech winaq’.\nJujun ke rajalanik’ ri chol q’ij ri ajayayib’ k’iche’ ruk’ ri waxaq’ik’ b’atz, waxajib’ batz’ ruk’ masis ri oxlaj k’al ri chpam le cholq’ij chipam ri kemajwaxaq’ib’ ri nojel q’ij ri cholq’ij ojer cholq’ij ri cholq’ij k’epaxanik’ jun mox ri ri masi k’ak le rech axlajk’al re q’ij xuquje rech maj rech /oxlajuj+juwinaq’ xuquje re le mayab’ ajilab’al rech lajuj –jun winaq’ri maj rech ri wasimajri chak re nim uk’il uwachre le k’aslemal rech k’eb’ b’elejeb’ ri cholq’ij julajk’aloxlajuj rech julajuj che jun winaq’ rech jolib’al rech ajmatyib’ rech rech jun winaq’ jun winaq’ rech q’ab’aj rech maj ri keb’oxlajuj pa kawinaq’ waq’b’xuquje julaj k’al kajlajuj.</p>', 'kiche', 'K\'utu\'nb\'al'),
(1, 'AnimacionOtro', 'Rueda Calendarica', 'Informacion', '<img class=\"rueda\" src=\"..\\imgs\\cuentaLarga.png\" >', 'kiche', 'K\'utu\'nb\'al'),
(2, 'EnergiaKiche', 'Calendario Cholquij', 'Elementos', '<p>\n  \r\n Ri chol q’ij are uchuq’ab’ rech jun q’ij pa le kab’lajujik rumal\n  k’aslemal nim, are uwach. r\n\n</p>\n\r\n\n\n<h2>Re le uwach ulew re nim q’ij:</h2>\n<ul>\n  <li>Ri kab’lajuj uchuq’ab’ rech k’aslemal</li>\n  <li>Ri kab’lajuj re jun winaq’ q¿ij rech jun njunab’rech chol q’ij.</li>\n</ul>\n<p>\n  Ri kab’lajuj q’ij are k’ichomaj ri jolom rech chomanik ajamyib’ are uchuq’ab’\n  rech ri nawal are k’uya uwach ri ri noj uwach ri winaq’.\n</p>\n', 'kiche', 'ch\'ob\'ojel'),
(1, 'K\'oje\' k\'oje\' chij', 'Mesoamerica', 'Informacion', '<p>Le Cholq\'ij taq maya ch\'aj le Cholq\'ij taq mexica k\'olik jun pa che\' k\'utunem taq che\' pa Mesoamérica. Ta kojlebal ruk\'uj jun k\'oje\' junal b\'e le xib\'al q\'ij taq che\' junab\'ix saqob\'al, ch\'aqa\' kan qaab\'al taq che\' re q\'utunemal.</p><div class=\"container\">\n    <div id=\"tabla\">\n        <table class=\"table table-dark table-striped\">\n            <thead>\n                <tr>\n                    <th scope=\"col\">K’alendario Maya</th>\n                    <th scope=\"col\">K’alendario Mexica</th>\n                </tr>\n            </thead>\n            <tbody>\n                <tr>\n                    <td> Ri k’alendario Maya k’o wi jun uk’uj ri tz’ij: ri Uk’exib’äl ch’ab’äl xane ri Tzolk’in.\n                        Ri Uk’exib’äl ch’ab’äl, chik xkib’ij, jun tzij k’o wi jun chi k’exik b’an chi keje junab’ k’a pa q’ij chi ch’ab’äl, che ri xoqatz’ib’ chi 11 ri winaq’ pa ri aq’b’äl 3114 winaq’.\n                        Ri Tzolk’in jun q’ij chi ruwi 260 q’ij xa ri kaib’ chi b’anoj ri 20 kaq’ij rech ri q’ij xe ri 13 junab’.\n                        Chi jun q’ij k’o jun b’anoj ri kab’an ri junab’, xe chub’anik chi 260 q’ij.</td>\n                    <td> Ri k’alendario Mexica k’o wi jun uk’uj ri tz’ij: ri Xiuhpohualli xane ri Tonalpohualli.\n                        Ri Xiuhpohualli jun q’ij chi ruwi 365 q’ij ch’ab’äl chi 18 u k’atun pa 20 q’ij xe 5 q’ij ch’ab’äl ri nemontemi.\n                        Chi jun k’atun k’o jun b’anoj xe ch’ab’äl chi jun wachib’äl rech ri ch’ab’äl k’aslem.\n                        Ri Tonalpohualli jun q’ij chi ruwi 260 q’ij xa ri kaib’ chi b’anoj ri 20 wachib’äl xe ri 13 junab’.\n                        Chi jun q’ij k’o jun b’anoj ri wachib’äl ri junab’, xe chub’anik chi 260 q’ij.</td>\n                </tr>\n            </tbody>\n        </table>\n    </div>\n</div><p>Xchi ri winaq’, che\' ja\' nim raqan, k\'ut k\'aslem, la jun xk\'utu\' k’alendario Maya ri k’alendario Mexica k’o wi jun chike\' le k’utu’l ri k’alendario Maya k’o chike\'le, ronoj le k’alendario Mexica k’o wi jun chike\' le k’utu’l.\n\nRi k’alendario Maya k’o wi ronoj le ch’utuj utz k\'ut uch’ux ri chi b’anoj ri q’ij k’o junab’ pa ruwi, k’ut k\'aslem ri k’alendario Mexica k’o wi junab’ ch’ab’äl chi jun keje taq chik xk\'utu\' le q\'ij, ja\' pa jun q\'ij xiweq’aj ch\'utuj le wi q\'ij xuwe.\n\nUtz chike\'le ronoj chik le k’alendario Maya chike’l jun utz q’ij xuweq’aj utz chik k\'aslem, ronoj la k’alendario mexica, xa utz chike\'le k\'uq\'k\'uxi\'kab\' utz k\'ut uch’ux ri chik, k\'ut chike\' utz ri chi xuweq’aj rech ri q’ij xa ja\' ri q’ij.\n\nXa\' uq’ij chike\' ri ri k’alendario Maya, xa ri tz’aqolb’al le winaq’ ri q’ij chi wok’ chi ri xa ri tz’aqolb’al le winaq’ ri q’ij chi k\'ot ri xk\'olb\'al q’ij xk\'utu\'lej chik ri tz\'aqolb’al, xchi uq’ij chike’ ri k’alendario Mexica xa ri tz’aqolb’al le winaq’ ri q’ij chi k’ot ri xk\'olb\'al q’ij chike\'le, xchike\'la uq’ij le xk\'olb\'al q’ij chi ri tz\'aqolb\'al, xchike’l uq’ij le winaq’ ri q’ij chi q’ij.\n</p>', 'kiche', 'K\'utu\'nb\'al'),
(1, 'k\'utal', 'Rueda Calendarica', 'Informacion', '<h2>K\'utal</h2>\n<p>U Rueda tzij re Calendario, utz k\'inaq\' 52 winalab\' re winaq\', ruk\' utz k\'inaq\' k\'ut.</p>\n<p>Jun b\'e\' ri winaq\' ronojel k\'inaq\' rueda calendárica, 8 Cauac 13 Zip, ruk\' ajil k\'inaq\' och b\'antajij k\'a trecena Cauac ri Tzolkin, k\'u xuquje ri k\'inaq\' 13 Zip ri Haab.</p>\n<p>U k\'utal k\'ot ri k\'inaq\' 52 winalab\' re 365 k\'u ri k\'inaq\' 52 winalab\' re 360 k\'u, k\'ot 260 k\'u utz; chik k\'u taj y-al ri chik winaq\' ri tzikinab\'äl k\'ot 260 k\'u, chik</p>\n<ul>\n<li>18 980 = 73 x 260</li>\n<li>18 720 = 72 x 260</li>\n<li>5 256 = 72 x 73 = 9 x 584.</li>\n</ul>\n<p>Ri chik ojer k\'u taj y-al chik 13 x 360 = 18 x 260 = 4 680.</p>\n<p>Utz, chik re ajk\'at\'a chik ri 5 winalab\' Uayeb\' chirachinaq\', xk\'oyb\'äl utz k\'u 13 xewaj re xk\'ut, (chik taj uk\'otaj chik k\'u re utz taj k\'utalab\' re 360 k\'u).</p>\n<p>Wan xk\'otaj, utz xk\'utalab\' re 52 xewaj chirachinaq\' chirachinaq\' taj 5 k\'otaj añoxib\' ri Wayeb\', chik ri 1 xewaj taj ulew k\'utalab\' re 260 k\'u.</p>\n<p>U rueda calendárica, chi k\'utal u kawinaq\' taq utzik\' chik k\'inaq\' re 52 winalab\'. Chi ronojel u tz\'ib\' re chik k\'inaq\' re winaq\' mayensej, xk\'ut chik ri nawalil náhuatl utz ronojel re mexica, \"Xiuhnelpilli\", taj k\'u\'k\'awal ri chi k\'utalab\' ronojel re añoxib\'.</p>\n<p>Chik k\'utalab\' re 52 winalab\', xk\'otaj chi k\'utalab\' re ajmayib\'. Chik ronojel u winaq\' re mexica, chirachinaq\' ri \"ronojel ri wajxal\" chirachinaq\'.</p>', 'kiche', 'K\'utu\'nb\'al'),
(1, 'Kins', 'Calendario Haab', 'Elementos', '<p>Ri tz’ib’aj ri nim taq winaq’ are umal k’onojel kawilo jas mo uwachri uwachrech’’glif’’’ are k’ub’antajij ruk jun tz’ij. ‘¿k’uya ruk’ jun ajmob’´¿ ruk. Ri glif’ are k’uya k’j b’antajij jun tz’ij ‘’ruk’ ri morfem’’ ruk’ ri uxol q’ij rech jun k’eb’ tz’ij.</p>', 'kiche', 'ch\'ob\'ojel'),
(1, 'Nawal', 'Calendario Cholquij', 'Elementos', '<p>Are k’eb’ uwach le b’iaj rech nagual xuquje k’ukub’ik’ rech le k’ib’antajij ri masis ri wach le noj wuj rech awaj ri winaq’ rech ri k’eb’ rech awaj rech nim. Ruk’ ri chab’anik’ le tinamit’ winaq’ rech aq’ij ruk’ le nim k’iwanik uwach ulew ri b’x ri ojer taq’ winaq’ ruk’ le ri waniq’ ri paq’. Rech le nim ajamayib’ jujun re le nim awaj rech jun wach ulew re le ajaw rech le xuquje re loe nim  ajaw re k’amalb’e ri chab’anik’  < /p>conoce los nahuales:</h2>\r\n<figure class=\"easyimage easyimage-full\"><img alt=\"\" src=\"../img/DiasMayas.png\"/>  <figcaption></figcaption>  </figure> ', 'kiche', 'ch\'ob\'ojel'),
(0, 'Relacion Mesoamericana', 'Mesoamerica', 'Informacion', '<p>El Calendario Maya y el Calendario Mexica son dos sistemas de medición del tiempo que se desarrollaron en Mesoamérica. Ambos se basan en ciclos astronómicos y matemáticos, pero tienen diferencias importantes en su estructura y funcionamiento.</p>    <div class=\"container\">\n        <div id=\"tabla\">\n            <table class=\"table table-dark table-striped\">\n                <thead>\n                    <tr>\n                        <th scope=\"col\">Calendario Maya</th>\n                        <th scope=\"col\"> Calendario Mexica</th>\n                    </tr>\n                </thead>\n                <tbody>\n                    <tr>\n                        <td> El Calendario Maya se compone de dos cuentas principales: la Cuenta larga y el Tzolkin.\n                            La Cuenta larga, como ya lo vimos, es una serie de números que indican la cantidad de días\n                            transcurridos desde un\n                            punto de inicio fijo, que se estima que corresponde al 11 de agosto del año 3114 A.N.E.\n                            El Tzolkin es un ciclo de 260 días formado por la combinación de 20 nombres de días y 13\n                            números. Cada día tiene un\n                            nombre y un número únicos, y se repite cada 260 días.</td>\n                        <td> El Calendario Mexica se compone de dos cuentas principales: el\n                            Xiuhpohualli y el Tonalpohualli.\n                            El Xiuhpohualli es un ciclo de 365 días dividido en 18 meses de 20 días cada uno, más cinco\n                            días adicionales\n                            llamados nemontemi.\n                            Cada mes tiene un nombre y está asociado a una festividad religiosa.\n                            El Tonalpohualli es un ciclo de 260 días formado por la combinación de 20 signos y 13\n                            números. Cada día tiene un\n                            signo y un número únicos, y se repite cada 260 días.</td>\n                    </tr>\n                </tbody>\n            </table>\n        </div>\n    </div><p>Como puedes ver, son muy similares, pero la principal diferencia entre el Calendario Maya y el Calendario Mexica es que el primero es lineal y el segundo es circular.\n\nEl Calendario Maya registra el tiempo desde un origen hasta un final, mientras que el Calendario Mexica representa el tiempo como una sucesión de ciclos que se renuevan constantemente.\n\nOtra diferencia es que el Calendario Maya tiene una mayor precisión astronómica que el calendario mexica, ya que incorpora ajustes para sincronizar los ciclos solares y lunares.\n\nPor último, una diferencia cultural es que el Calendario Maya refleja la cosmovisión de los antiguos Mayas, que concebían el tiempo como una manifestación del orden divino, mientras que el Calendario Mexica refleja la cosmovisión de los mexicas, que concebían el tiempo como una fuerza dinámica y conflictiva.</p>', 'espaniol', 'K\'utu\'nb\'al'),
(0, 'Uchik\'ab\'ik\'', 'Calendario Cholquij', 'Informacion', '<h2>Uchik\'ab\'ik\'</h2>Jujun ke rajalanik’ ri chol q’ij ri ajayayib’ k’iche’ ruk’ ri waxaq’ik’ b’atz, waxajib’ batz’ ruk’ masis ri oxlaj k’al ri chpam le cholq’ij chipam ri kemajwaxaq’ib’ ri nojel q’ij ri cholq’ij ojer cholq’ij ri cholq’ij k’epaxanik’ jun mox ri ri masi k’ak le rech axlajk’al re q’ij xuquje rech maj rech /oxlajuj+juwinaq’ xuquje re le mayab’ ajilab’al rech lajuj –jun winaq’ri maj rech ri wasimajri chak re nim uk’il uwachre le k’aslemal rech k’eb’ b’elejeb’ ri cholq’ij julajk’aloxlajuj rech julajuj che jun winaq’ rech jolib’al rech ajmatyib’ rech rech jun winaq’ jun winaq’ rech q’ab’aj rech maj ri keb’oxlajuj pa kawinaq’ waq’b’xuquje julaj k’al kajlajuj.</p>\n', 'kiche', 'K\'utu\'nb\'al'),
(2, 'unil', 'Calendario Haab', 'Elementos', '<p> Ri unilb’a kwilo ruk rech taq ik’ taq’ re jun juwinaq q’ij, ruk’ re le uxol q’ij k’ab’lajuj jujun rech cholq’ij hab’ mayeb’ jujun wajxalajuj utz k’axlajenik’ raq’ lexik’ ri nojel q’ij. </p>', 'kiche', 'ch\'ob\'ojel'),
(3, 'Uyeb', 'Calendario Haab', 'Elementos', '<p>Pa ri ojer q’ij rech chol q’ij hab’ ri are ri ik’ b’elejeb’ ub’i uyeb’ are ri q’ij sib’alj utz k’ujjilb’ik’ pa le wachch.</p>', 'kiche', 'ch\'ob\'ojel'),
(0, 'Wuj tz’ij', 'Calendario Haab', 'Informacion', '<p>Ri chol q’ij q’ij rech ajmayib’ ub’i hab’, are ri uchuq’ab’ rech wajxaqlajk’al job’ xuquje ixpetik’ ri cholq’ij ija’ re ajamayib’ ri nim taq winaq’ rech nim ja’.\nRi tz’ij ‘’ hab’ ‘’ ari re ik’ rech pa q’atz’ij rech yucatek’.\nEl hab’ are k’uj b’antajij rech wajxalujuj xuquje juwinaq’ re ik’ jujun are job’ ik. Rech ik’ juweinaq’ ri q’ij taq are k’ewetamaj ruk le ub’i unilare k’o ri ub’i nojel taq wajib’al rech wajxaqlajk’al.\nRi maj ik’ are k’ub’an rech job’ ub’i are wayeb’, b’ejlajuj are k’ub’an re ik\nWaqxaljuj + ju winaq’ +--wajxaqlajuk’al job’.\nRi min taq winaq’ xuquje ri wajxalijak’ ajmayib’ are xu ya che le winaq re wa’ ruk’ ri ija’ rech ulew xuquje ri k’inaq’ k’onojel taq’ rech wa’ re rech ajk’amalb’e re tinamit’.\nLe cholq’ij, ri taq’ nim winaq’ are ri k’ub’an ri chuxol ri nim q’ij rech juq’o’ wuqk’al lajuj exk’ori are xu k’aslemal ruk’ le tz’ik’in rech jujun ik’sib’alaj nimrech kawinaq’ k’ab’lajuj ik’ ri cholq’ij rech nim taq’ winaq’ taq rech rech waq’ij rech mesoa mesoamerik’,.\nK’ab’tajij.\nRi chol q’ij hab’ k’jtab’ij rech nim ik’ tak xuquje re urilik’ ri ik’ taj\n*xulb’ij ik’\n* ri ik’ rech q’ij\nXuquje k’ajtajib’ che le k’ak’ tz’ij rech taq’ nim ajk’amalb’e tinamit’, are ri uk’ab’ q’ij kub’antajik’ ri ajq’ij ri q’aq’ rech rui watit’ kamam ch ri ajw che le nim tikonik’ are ri jun pa k’il uwach rechri ik’.\nJe’lik’ ajtik’\nRi nim q’ij k’il pa le uwach ukew ri sib’alaj ik’ pa nim chaq’ab’ k’uril taq’ pa le uwach ulew rech wajxalajujk’al kajib’ q’ij =wajxalajujk’al kajib’. Chi oxlajuj  chi junil waq’ib’ q’ij *, re ri k’ajb’tik’ ri wuj tz’ib’exik’ ri junpa ri uwach k’uxa le jujun wach chuch le wuj.+\nRi ojer taq winaq ajmayib’k’i chomaj jas mo uwach uwach q’ij jujun q’ijk’ajib’ rech oxlajuj xuquje kawinaq’ k’ab’lajuj ik.\nKuj kwilo k’uj chomaj rech le chol q’ij hab’ xuquje k’olik jun mach jun q’ij rech waxak’ib’b’elejeb’ ik’. </p>', 'kiche', 'K\'utu\'nb\'al');



